	def parse(self, response):
		items = []
		links = LinkExtractor(canonicalize=True, unique=True).extract_links(response)
		for link in links:
			item = DomainsItem()
			item['url_from'] = response.url
			item['url_to'] = link.url
			parsed_uri = urlparse(link.url)
			item['host_url'] = '{uri.netloc}'.format(uri=parsed_uri)
			# sorted_tlds = self.tlds.sort(key=len)
			sorted_tlds = sorted(self.tlds, key=len)
			for tld in sorted_tlds:
				tmp_tld = item['host_url'][-len(tld):]						
				if tmp_tld.lower() == tld.lower():
					tld_new = tld.replace('.', '')
					domainLeft = item['host_url'][0:-(len(tld_new)+1):]
					pos = domainLeft.find('.')
					if pos == -1:
						subDomain = ''
						finalDomain = domainLeft
					else:
						domain_parts = domainLeft.split('.')
						finalDomain = domain_parts.pop()
						subDomain = '.'.join(domain_parts)
					item['domain'] = finalDomain
					item['sub_domain'] = subDomain
					item['tld'] = tld_new
			items.append(item)
			sub_items = scrapy.Request(link.url, callback=self.parse)
			items.append(sub_items)
		return items 
